                    this.skipForward();
                    if (!this.paused) {
                        this.play();
                    }
                }, 2000);
            } else if (this.list.audios.length === 1) {
                this.notice('An audio error has occurred.');
            }
        });
        this.events.on('listswitch', () => {
            skipTime && clearTimeout(skipTime);
        });

